```csharp
public class Stack
{
    private int[] _data;
    private int _top;

    public Stack(int capacity)
    {
        _data = new int[capacity];
        _top = -1;
    }

    public void Push(int value)
    {
        if (_top == _data.Length - 1)
        {
            throw new InvalidOperationException("Stack is full");
        }
        _data[++_top] = value;
    }

    public int Pop()
    {
        if (_top == -1)
        {
            throw new InvalidOperationException("Stack is empty");
        }
        return _data[_top--];
    }
}
```

